html:
{%- macro add_header(page_num, max_pages, doc, letter_head, no_letterhead) -%}
    {% if letter_head and not no_letterhead %}
    <div class="letter-head">{{ letter_head }}</div>
    {% endif %}
    {%- if doc.meta.is_submittable and doc.docstatus==0-%}
    <div class="alert alert-info text-center">
        <h4 style="margin: 0px;">{{ _("DRAFT") }}</h4></div>
    {%- endif -%}
    {%- if doc.meta.is_submittable and doc.docstatus==2-%}
    <div class="alert alert-danger text-center">
        <h4 style="margin: 0px;">{{ _("CANCELLED") }}</h4></div>
    {%- endif -%}
    {% if max_pages > 1 %}
    <p class="text-right">{{ _("Page #{0} of {1}").format(page_num, max_pages) }}</p>
    {% endif %}
{%- endmacro -%}

<style>
    .table-hover>tbody>tr:hover>td, .table-hover>tbody>tr:hover>th {
    background-color: #B7CCF2;
    color:#000000;
    font-size:16px;
    }
    .print-format table, .print-format tr, 
    .print-format td, .print-format div, .print-format p {
        font-family: sans-serif;
        line-height: 90%;
        vertical-align: middle;
    }
    @media screen {
        .print-format {
            width: 210mm;
            padding: 10mm;
            min-height: 297mm;
            footer {page-break-after: always;}
        }
    }
</style>

<small>
{{ add_header(0,1,doc,letter_head, no_letterhead) }}
<table class="table table-condensed table-bordered">
  <tr>
    <td colspan="2">
        {% if doc.payment_type == 'Pay' %}
            <b style="font-size:16px;">{{ doc.select_print_heading or (doc.print_heading if doc.print_heading != None
            else _("Payment Voucher")) }}</b>
        {% elif doc.payment_type == 'Receive' %}
            <b style="font-size:16px;">{{ doc.select_print_heading or (doc.print_heading if doc.print_heading != None
            else _("Payment Receipt")) }}</b>
        {% else %}
            <b style="font-size:16px;">{{ doc.select_print_heading or (doc.print_heading if doc.print_heading != None
            else _(doc.doctype)) }}</b>
        {% endif %}
    </td>
    <td style="width:30%">          
        <b style="font-size:16px; text-align:left; vertical-align:middle;">{{ _("# ") }}{{ doc.name }}</b><br>
    </td>
  </tr>
</table>

<table class = "table table-condensed table-bordered">
  <tr>
    <td style="width: 70%;">
        <b style="font-size:16px;">
        {% if doc.payment_type == 'Pay' %}
            {{ _("Paid To") }}: {{ doc.party_name }}</b><br>
        {% elif doc.payment_type == 'Receive' %}
            {{ _("Received From") }}: {{ doc.party_name }}</b><br>
        {% else %}
            {{ __("Internal Transfer") }} </b><br>
        {% endif %}
        <b style="font-size:16px;">
        {% if doc.payment_type == 'Pay' %}
            {{ _("Paid Amount") }}: 
        {% elif doc.payment_type == 'Receive' %}
            {{ _("Received Amount") }}: 
        {% else %}
            {{ _("Amount") }}: 
        {% endif %}
            {{ doc.get_formatted("paid_amount") }}</b><br>
        <b style="font-size:16px;">{{ _("Total Adjusted") }}: {{ doc.get_formatted("total_allocated_amount") }} </b>
    </td>
    <td style="width: 30%;">
        <b>{{ _("Ref No") }}:</b> {{ doc.reference_no }}<br>
        <b>{{ _("Ref Date") }}:</b> {{ doc.get_formatted("reference_date") }}
    </td>
  </tr>
</table>
{% if doc.references %}
<table class="table table-condensed table-hover table-bordered">
        <tr>
            <th>Sr</th>
            <th>Our Ref#</th>
            <th>Our Ref Date</th>
            <th class="text-right">Invoice or PO #</th>
            <th>Your Ref Date</th>
            <th class="text-right">Total</th>
            <th class="text-right">Paid/Received</th>
        </tr>
        {%- for row in doc.references -%}
        <tr>
            <td style="width: 3%;">{{ row.idx }}</td>
            <td style="width: 22%;">{{ row.reference_name }}</td>
            <td style="width: 10%;">
                {% set posting_date = frappe.db.get_value(row.reference_doctype, row.reference_name, "posting_date") %}
                {{ frappe.format_value(posting_date, doc.meta.get_field("posting_date"), doc) }}</td>
            {% if row.reference_doctype == "Purchase Invoice" and not no_letterhead %}
                <td style="width: 25%; text-align: right;">{{ frappe.db.get_value(row.reference_doctype, row.reference_name, "bill_no") }}</td>
                <td style="width: 10%; text-align: right;">
                    {% set bill_date = frappe.db.get_value(row.reference_doctype, row.reference_name, "bill_date") %}
                    {{ frappe.format_value(bill_date, doc.meta.get_field("posting_date"), doc) }}</td>
            {% endif %}
            {% if row.reference_doctype == "Sales Invoice" and not no_letterhead %}
                <td style="width: 25%; text-align: right;">
                    {{ frappe.db.get_value(row.reference_doctype, row.reference_name, "po_no")}}</td>
                <td style="width: 10%; text-align: right;">
                    {% set po_date = frappe.db.get_value(row.reference_doctype, row.reference_name, "po_date") %}
                    {{ frappe.format_value(po_date, doc.meta.get_field("posting_date"), doc) }}</td>
            {% endif %}
            <td style="width: 15%; text-align: right;">
                {{ row.get_formatted("total_amount") }}</td>
            <td style="width: 15%; text-align: right;">
                {{ row.get_formatted("allocated_amount") }}</td>
        </tr>
        {%- endfor -%}
    </tbody>
</table>
{% endif %}
